module Task6 (sol1, sol2) where

import Data.List(nub)

sol1 :: Int
sol1 = solve 4

sol2 :: Int
sol2 = solve 14

solve :: Int -> Int
solve target = findMarker (take (target-1) input) (drop (target-1) input) target
  where findMarker :: String -> String -> Int -> Int
        findMarker (a:as) (x:xs) n = if length (nub (x:a:as)) == target then n else findMarker(as ++ [x]) xs (n+1)

input :: String
input = ""
