module Task6 (sol1, sol2) where

import Data.List(nub)

sol1 :: Int
sol1 = solve 4

sol2 :: Int
sol2 = solve 14

solve :: Int -> Int
solve n = findMarker (take (n-1) input) (drop (n-1) input) n n

findMarker :: String -> String -> Int -> Int -> Int
findMarker (a:as) (x:xs) n t = if length (nub (x:a:as)) == t then n else findMarker (as ++ [x]) xs (n+1) t

input :: String
input = ""
